-- Listar los datos de los autores.
SELECT * FROM AUTOR;

-- Listar nombre y edad de los estudiantes
SELECT nombre, edad FROM ESTUDIANTE;

-- ¿Qué estudiantes pertenecen a la carrera informática?
SELECT * FROM ESTUDIANTE WHERE carrera = 'Ing. Sistemas';

-- ¿Qué autores son de nacionalidad francesa o italiana?
SELECT * FROM AUTOR WHERE nacionalidad = 'Francesa' OR nacionalidad = 'Italiana';

-- ¿Qué libros no son del área de internet?
SELECT * FROM LIBRO WHERE area != 'internet';

-- Listar los libros de la editorial Salamandra.
SELECT * FROM LIBRO WHERE editorial = 'Salamandra';

-- Listar los datos de los estudiantes cuya edad es mayor al promedio.
SELECT * 
FROM ESTUDIANTE es 
WHERE es.edad > (SELECT AVG(edad) FROM ESTUDIANTE);

-- Listar los nombres de los estudiantes cuyo apellido comience con la letra G.
SELECT * 
FROM ESTUDIANTE
WHERE apellido LIKE 'G%';

-- Listar los autores del libro “El principito”. (Se debe listar solamente los nombres).
SELECT au.nombre 
FROM libro_autor la
INNER JOIN AUTOR au ON la.id_autor = au.id_autor
INNER JOIN LIBRO li ON la.id_libro = li.id_libro
WHERE li.titulo = 'El principito';

-- ¿Qué libros se prestaron al lector “Juan”?
SELECT li.titulo 
FROM PRESTAMO p
INNER JOIN LIBRO li ON li.id_libro = p.id_lector
INNER JOIN ESTUDIANTE es ON es.id_estudiante = p.id_lector
WHERE es.nombre = 'Juan';

-- Listar el nombre del estudiante de menor edad.
SELECT nombre 
FROM ESTUDIANTE es 
WHERE es.edad < 18;

-- Listar nombres de los estudiantes a los que se prestaron libros de Base de Datos.
SELECT es.nombre
FROM PRESTAMO p
INNER JOIN LIBRO li ON li.id_libro = p.id_lector
INNER JOIN ESTUDIANTE es ON es.id_estudiante = p.id_lector;

-- Listar los libros que pertenecen a la autora Antoine de Saint-Exupéry.
SELECT li.titulo 
FROM libro_autor la
INNER JOIN AUTOR au ON la.id_autor = au.id_autor
INNER JOIN LIBRO li ON la.id_libro = li.id_libro
WHERE au.nombre = 'Antoine de Saint-Exupéry';

-- Listar títulos de los libros que debían devolverse el 16/07/2021.
SELECT li.titulo, p.fecha_devolucion
FROM LIBRO li
INNER JOIN PRESTAMO p ON p.id_libro = li.id_libro
WHERE fecha_devolucion = '2021-07-16';